(function($) {
	
	"use strict";
	
	/* :: Themeva Text Slider						      
	---------------------------------------------*/		

	var themevaTextSlider = function()
	{
		var container	= '.textslider-wrap';

		$( container ).each(function(index, value)
		{ 				
			var	sliderObj	= $(this),
				slider	 	= '#'+ sliderObj.attr('id'),	
				effect		= sliderObj.attr('data-texteffect'),
				timeout		= sliderObj.attr('data-texttimeout');	
				
			$( slider +' .textslider-slides' ).cycle({ 
				fx: effect,
				timeout: timeout,
				speed: 1000,
				slideResize: 1,				
				cleartype:  true,
				cleartypeNoBg:  true,
				easing: 'easeInOutExpo',
				prev: slider + ' .slidernav-left',
				next: slider + ' .slidernav-right'
			});	
		
		});
	}
		
	jQuery(window).load( function()
	{
		themevaTextSlider();		
	});
	
})(jQuery);		